css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

.bubbles ._kso {

background-color: rgb(0,132,255)!important;
background-image: -moz-linear-gradient(top, #57A0FF 0%, #0084FF 100%) !important;
background-image: -webkit-linear-gradient(top, #57A0FF 0%, #0084FF 100%)!important;
box-shadow: none !important;
}
.bubbles ._kso a {
color: rgb(235,2355,235) !important;
text-decoration: underline;
}

.bubbles div._kso {
color: white !important;
text-shadow: none !important;
border-radius: 8px !important;
}

.bubbles ._50kd ._kso {
background-color: rgb(241,239,240)!important;
background-image: none !important;
color: #333 !important;
}
.bubbles ._50kd ._kso a{
color: grey !important;
text-decoration: underline;
}
.fbNubFlyoutBody {
background: white !important;
}

.bubbles ._kso:after,.bubbles ._kso:before {
content: none !important;
}
}
