css:
@-moz-document domain("forums.nexusmods.com") {
#backtotop img{
  	content:url(http://forums.nexusmods.com/public/style_images/underground/top.png);
  	height: 17px;
  	width: 17px;
}

.topic_buttons li.non_button a img {
  	content:url(http://forums.nexusmods.com/public/style_images/underground/icon_check.png);
}

.ipsButton_extra img {
	content:url(http://forums.nexusmods.com/public/style_images/underground/icon_users.png);
}

.desc img {
    content:url(http://forums.nexusmods.com/public/style_images/underground/icon_tag.png);
}
    
#ajax_loading img {
	content:url(http://forums.nexusmods.com/public/style_images/underground/ajax_loading.gif);
}

#notify_link img {
	background: url(http://forums.nexusmods.com/public/style_images/underground/icon_notify.png) no-repeat top;
}

#user_link {
  	padding-left: 50px !important;
	background-image: url('http://i2.wp.com/forums.nexusmods.com/public/style_images/white/profile/default_large.png') !important;
  	background-repeat: no-repeat !important;
  	background-size: 40px 40px !important;
 	padding-left: 50px !important;
	padding-right: 0 !important;
}

#notify_link {
	background: url('http://forums.nexusmods.com/public/style_images/underground/icon_notify.png') no-repeat top !important;
  	background-position: 10px 13px !important;
}

#inbox_link {
	background: url('http://forums.nexusmods.com/public/style_images/underground/icon_inbox.png') no-repeat top !important;
  	background-position: 10px 13px !important;
}

.unread .col_c_icon a img {
	content:url(http://community.invisionpower.com/filestore/public/style_images/master/f_icon.png);
}

.col_c_icon img {
    content:url(http://community.invisionpower.com/filestore/public/style_images/master/f_icon_read.png);
}
}

@-moz-document domain("forums.nexusmods.com") {
#forum_table tr:hover {
  	background-color:#E2E9F0;
}
}
