@-moz-document domain('magumataishi.cocolog-nifty.com') {
#container {width: auto !important;}
#container-inner {background-size: 120% auto !important;}

#banner {height: auto !important;}
#banner-inner {width: auto !important;}

#pagebody-inner {width: auto !important;}
#pagebody-inner #beta {width: 100% !important; margin: 0 -200px !important;}
#pagebody-inner #beta #beta-inner {width: auto !important; margin: 0 200px !important;}
#pagebody-inner #beta #beta-inner .entry {width: auto !important;}
#pagebody-inner #beta #beta-inner .archive,
#pagebody-inner #beta #beta-inner .trackbacks,
#pagebody-inner #beta #beta-inner .comments,
#pagebody-inner #beta #beta-inner .archive,
#pagebody-inner #beta #beta-inner .comments-open
 {width: auto !important;}
.layout-three-column #comments-open-data input,
.layout-three-column #comments-open-text textarea
 {width: 100% !important;}
}
