css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

div#content {margin-left:0px !important}


/*left navigation*/
div#mw-panel.collapsible-nav, div#mw-panel {
  margin-left:-168px !important;
  transition:margin .5s .5s !important;
  border-right:15px solid !important;
  border-bottom:1px solid !important;
  border-right-color:rgba(6,69,173,.25) !important;
  border-bottom-color:rgba(6,69,173,.25) !important;
  background-color:white !important;
    position:fixed !important;
    top:0px !important;
    max-height:90% !important;
    overflow-y:auto !important;
    overflow-x:hidden !important;
}

div#mw-panel.collapsible-nav:hover, div#mw-panel:hover {
margin-left:0px !important;
transition:margin .5s .5s !important;
}

}
