css:
@-moz-document domain("docs.google.com") {
.kix-cursor-caret
{
  opacity: 0.3;
}
.kix-cursor-top
{
  display:none !important;
}
.kix-cursor-name
{
  opacity: 0 !important;
}
.kix-cursor-name:hover
{
  opacity: 1 !important;
}

.kix-selection-overlay
{
  display:none;
}
 
.kix-selection-overlay[style*="background-color: rgb(0, 0, 0)"],
.kix-selection-overlay[style^="top"]
{
  display:inline !important;
}
}
