css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blogs.technet.com") 
{

html, LI.content-item, body, DIV.post-content.user-defined-markup, DIV#ctl00_content_ctl00_page.content-fragment-page.post, DIV.full-post,
.content-fragment.captcha-blog-post-comment-form.no-wrapper-with-spacing,
DIV#ctl00_content_ctl00_w_101500__b63e95_ctl00_ctl00_DelayedFeedbackList, DIV.layout-region-inner.content,
.searchField, DIV.layout-region-inner.right-sidebar, #search-thisblog-container, .field-list, .layout,
#search-thisblog-container > span:nth-child(1),field-list-footer, span, #search-switch-container,
DIV.content-fragment-content, UL.tag-list, .user-defined-markup,
.profile-leaderboard-container, profile-leaderboard-item, .profile-leaderboard-header,
DIV#megabladeContainer
{
  background-color: rgb( 15, 15, 15 ) !important;
  color: rgb( 180, 180, 180 ) !important;
}

H2.label, profile-leaderboard-header
{
  border-style: none !important;
}


DIV#search-thisblog-container, SPAN.field-item-input
{
  border-color: rgb( 250, 150, 20 ) !important;  
}

DIV.full-post, H3.post-name, SPAN.value, H1,  a, .blog-banner-name
{
  color: rgb( 100, 150, 210 ) !important;
}

DIV.header-fragments
{
  border-bottom-color: rgb( 10, 150, 210 ) !important;
}

DIV.share-widgets, .footer-fragment.copyright-info
{
  border-top-style: solid !important;
  border-top-width: 1px !important;
  border-bottom-style: solid !important;  
  border-bottom-width: 1px !important;  
  border-color: rgb( 10, 150, 210 ) !important;
}

DIV.blog-banner-description, .field-list-description, div, .label, H2
{
  color: rgb( 250, 150, 20 ) !important;  
}

.footer-fragment.copyright-info, .footer-fragment-links, li
{
  background-color: rgb( 15, 15, 15 ) !important;
  color: rgb( 180, 180, 180 ) !important;  
}



.fiji-post-author-by 
{
 padding-bottom: 0px !important;

}

.navdownarrow
{
  background-color: transparent !important;  
}


DIV#blog-nav, DIV.breadcrumb-list, div
{
  background-color: transparent !important; 
  border-style: none !important
}

a.selected, a.selected > span:nth-child(1)
{
  background-color: rgb( 250, 150, 20 ) !important;  
  color: black !important;
}

UL.field-list
{
  border-color: rgb( 250, 150, 20 ) !important; 
}

p > a:link, span > a:link, ul>link
{
  text-decoration:underline !important;
}


}
