#vt100 #scrollable {
	color: #F00!important;
	background-color: #000!important;
}
#vt100 #cursor.bright {
	background-color: #F00!important;
}
#vt100 .bgAnsi0 {
	background-color: #6D0000!important;
}
#vt100 .ansi15 {
	color: #BEBEBE!important;
}
#vt100 #cursor.inactive {
	background: #000 !important;
	background: -moz-linear-gradient(center top, #333333, #000000) repeat scroll 0 0 #000000 !important;
	background: -webkit-gradient(linear, left top, left bottom, from(#AD0000), to(#000000)) !important;
	background-color: #303030 !important;
	border: 0px outset #000000 !important;
	border-color: #FF0000 !important;
	box-shadow: 0 0 10px #FF0000 !important;
}
#vt100 #cursor.bright {
	background: #000 !important;
	background: -moz-linear-gradient(center top, #000000, #333333) repeat scroll 0 0 #000000 !important;
	background: -webkit-gradient(linear, left top, left bottom, from(#000000), to(#333333)) !important;
	background-color: #303030 !important;
	border: 0px outset #000000 !important;
	border-color: #FF0000 !important;
	box-shadow: 0 0 10px #FF0000 !important;
}
#vt100 #menu .popup {
	background: #000 !important;
	background: -moz-linear-gradient(center top, #460000, #000000) repeat scroll 0 0 #000000 !important;
	background: -webkit-gradient(linear, left top, left bottom, from(#460000), to(#000000)) !important;
	background-color: #303030 !important;
	border-color: #640000 !important;
	box-shadow: 0 0 42px #000000 !important;
	border-radius: 7px !important;
	outline: none !important;
