css:
@-moz-document url-prefix("http://www.acfun.com"),url-prefix("http://www.acfun.tv"){
div#ad-top-index
{
  display:none!important;
}
}

@-moz-document url-prefix("http://www.acfun.com/v/ac"),url-prefix("http://www.acfun.tv/v/ac"){
div#area-article-bottom,
div#area-video-other,
div#area-comment,
span.alert-warning,
div#block-guide-footer,
div#footer,
div#guide-space,
div#area-editor,
div#block-tool-footer,
div#area-tool,
div.ad,
div#ad-player-view,
div#area-toolbar-view,
div#area-bottom-view,
div#sub-guide
{
  display:none!important;
}

#title-article {
width: 100% !important;
}

div#guide, 
div#guide-inner
{
height:36px !important;
}
}

@-moz-document url-prefix("http://www.acfun.com/v"),url-prefix("http://www.acfun.tv/v"){
div.ad
{
  display:none!important;
}
}
