css:
@-moz-document url("http://dobrochan.com/bookmarks"), url("http://dobrochan.org/bookmarks"), url("http://dobrochan.ru/bookmarks") {
body > .logo {display:none !important;}
tr.logo {
    font-size:96% !important;
    text-align:left !important;
}
.adminbar, .stylebar, .footer {opacity:.5 !important;}
tr.logo td {    
    display:inline !important; 
    margin:1px 0  1px 50px !important;
    font-size:100% !important;
}

tr.logo > td a {text-decoration:none !important;}
.threadlist {border:none !important;}
tr.highlight {font-size:96% !important;}

}
