css:
@-moz-document domain("songza.com") {
.container{
    width: 100%;
}
#header{
	display:none;
}
.sz-nav-main>.container>.szi-nav{
  display:none;
}

#player{
   width: 100%;
}
#player .player-state{	
	width: 450px;
}
#player .szi-song-display{
width: 100%;
}
#side-div{
	display:none;
}
#content .container{
	width: 100%;
}
#main-div{
	width:100%;
    margin-left:-10px;
}
#main-div .szi-comments{
	display:none;
}
#main-div .sz-station-context{
	display: none;
}
#main-div .szi-recent-listeners{
	display: none;
}
#main-div .szi-station-quilt-image img{
	width: 600px;
  height:600px;
}
#main-div .sz-station-song-displayed .szi-details{
	padding-left: 330px;
}
#main-div .sz-station-song-displayed .szi-details h1{
	display: none;
}
#main-div .sz-station-song-displayed .szi-details .szi-meta .szi-current-song{
	font-size: 50px;
}
#main-div .sz-station-song-displayed .szi-details .szi-description{
	display: none;
}
#desk-email{
	display: none;
}
}
