css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.vector.co.jp") {  
  /* 上部広告非表示（領域は残るので別途調整が必要） */
  div#ad_vectorlarge { display: none; }
  body { background: none !important; }
  div#v_header>div.wrapper { background: #BCE3F6 !important; }
  div#v_header>div#v_search { background: #BCE3F6 !important; top: 72px !important; }
  div#lib_tab { background: #AFD549 !important; top: 120px !important; }
  #v_wrapper { padding-top: 145px !important; }

  /* ソフト詳細：コメントを見るボタン右広告非表示（および空領域調整） */
  #ad_dlsquare { display: none; }
  #v_inwrap .box_keywords { top: 50px !important; }
  #v_inwrap { padding-top: 100px !important; }
  
  /* 下部Google広告非表示 */
  .gad_wrap { display: none; }
  
  /* 下部ゲーム広告非表示 */
  #v_main>div[style='margin:20px 0;text-align:center;'] { display: none; }
  
  /* 左メニュー最下部広告非表示 */
  .leftad,
  #ad_listing2 { display: none; }
}
