css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stormfront.org") {

body{
    background: none repeat scroll 0% 0% rgb(0, 0, 0) !important;
}

a:link, body_alink {
    color: rgb(228, 0, 0) !important;
    font-weight: bold !important;
}

a:visited {
    color: rgb(188, 35, 0) !important;
}


.alt1, .alt1Active{ 
background: none repeat scroll 0% 0% rgb(0, 0, 0) !important;
color: rgb(188, 188, 188) !important;
}

.alt2, .alt2Active{
    background: none repeat scroll 0% 0% rgb(0, 0, 0) !important;
    color: rgb(188, 188, 188) !important;
} 

.page{
    background: none repeat scroll 0% 0% rgb(0, 0, 0) !important;
}

}
