css:
@-moz-document url-prefix("https://www.cryptsy.com/") {
body,
div,
footer,
#sidebar .moduletable .moduletable-header,
a,
.navbar-default .navbar-nav .active a:before, .navbar-default .navbar-nav .active a:after,
form.middle-forms fieldset ol li,
.panel-market-list-small .submit-holder,
.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th,
.table-striped > tbody > tr:nth-child(even) > td, .table-striped > tbody > tr:nth-child(even) > th,
table.table-bal span,
.form-control 
{
  background: #333 !important;
  color: #888 !important;
  text-shadow: none !important;
}

table.table-bal span[style="color:#0000FF;"]
{
  color: #428bca !important; 
}


li span {
  color: #888 !important;
  text-shadow: none !important;
}

.page .panel .panel-heading,
.panel-default
{
  color: #aaa;
}
    
.panel-default
{
  border: solid 1px #444;
}




div#tradechart {
  background-color: black;
}
div.highcharts-container {
  opacity: .4;
}


.success .messages {
background: #cee790 url(https://www.cryptsy.com/images/icon/success.png) no-repeat 20px 9px !important;
}
}
