css:
/* AGENT_SHEET */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace html url("http://www.w3.org/1999/xhtml");
scrollbar,slider,scrollbar>slider,scrollbar>slider>thumb,scrollbar>scrollbarbutton,scrollbarbutton{-moz-appearance:none!important;border:none!important}
scrollbar[orient='vertical']{
  background-color:transparent!important;
  background-image:linear-gradient(to right,hsla(212,100%,88%,.1) 1px,rgba(0,0,0,.3) 1px,rgba(0,0,0,.1) 40%,rgba(0,0,0,.1) 60%,rgba(0,0,0,.5) calc(100% - 1px),hsla(212,100%,88%,.1) calc(100% - 1px)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  background-size:100% 100%,193px 93px!important;
  background-position:0 0,0 0!important;
  background-repeat:no-repeat,repeat!important;
  background-attachment:local,local!important;
}
scrollbar[orient='horizontal']{
  background-color:transparent!important;
  background-image:linear-gradient(hsla(212,100%,88%,.1) 1px,rgba(0,0,0,.3) 1px,rgba(0,0,0,.1) 40%,rgba(0,0,0,.1) 60%,rgba(0,0,0,.5) calc(100% - 1px),hsla(212,100%,88%,.1) calc(100% - 1px)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  background-size:100% 100%,193px 93px!important;
  background-position:0 0,0 0!important;
  background-repeat:no-repeat,repeat!important;
  background-attachment:local,local!important;
}
scrollbar thumb[orient="vertical"]{
  min-height:20px!important;
  min-width:16px!important;
  width:16px!important;
  max-width:16px!important;
  background-color:transparent!important;
  background-image:linear-gradient(to right,hsla(212,100%,4%,0),hsla(212,100%,4%,.4)),linear-gradient(hsla(212,100%,4%,.3),hsla(212,100%,90%,.1),hsla(212,100%,4%,.3)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  background-size:100% 100%,100% 100%,193px 93px!important;
  background-position:0 0,0 0,0 0!important;
  background-repeat:no-repeat,no-repeat,repeat!important;
  background-attachment:local,local,local!important;
  box-shadow:0 0 0 1px hsla(212,100%,82%,.12) inset,0 0 0 1px hsla(212,100%,4%,.9),0 0 6px hsla(212,100%,4%,.6)!important;
}
scrollbar thumb[orient="vertical"]:hover,scrollbar thumb[orient="vertical"]:active{
  background-image:linear-gradient(to right,hsla(212,100%,4%,0),hsla(212,100%,4%,.2)),linear-gradient(hsla(212,100%,6%,.06),hsla(212,100%,90%,.1),hsla(212,100%,6%,.06)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  box-shadow:0 0 0 1px hsla(212,100%,82%,.12) inset,0 0 0 1px hsla(212,100%,4%,.9),0 0 6px hsla(212,100%,82%,.8)!important;
}
scrollbar thumb[orient="horizontal"]{
  min-width:20px!important;
  min-height:16px!important;
  height:16px!important;
  max-height:16px!important;
  background-color:transparent!important;
  background-image:linear-gradient(hsla(212,100%,4%,0),hsla(212,100%,4%,.4)),linear-gradient(to right,hsla(212,100%,4%,.3),hsla(212,100%,90%,.1),hsla(212,100%,4%,.3)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  background-size:100% 100%,100% 100%,193px 93px!important;
  background-position:0 0,0 0,0 0!important;
  background-repeat:no-repeat,no-repeat,repeat!important;
  background-attachment:local,local,local!important;
  box-shadow:0 0 0 1px hsla(212,100%,82%,.12) inset,0 0 0 1px hsla(212,100%,4%,.9),0 0 6px hsla(212,100%,4%,.6)!important;
}
scrollbar thumb[orient="horizontal"]:hover,scrollbar thumb[orient="horizontal"]:active{
  background-image:linear-gradient(hsla(212,100%,4%,0),hsla(212,100%,4%,.2)),linear-gradient(to right,hsla(212,100%,6%,.06),hsla(212,100%,90%,.1),hsla(212,100%,6%,.06)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  box-shadow:0 0 0 1px hsla(212,100%,82%,.12) inset,0 0 0 1px hsla(212,100%,4%,.9),0 0 6px hsla(212,100%,82%,.8)!important;
}
scrollcorner{
  background-color:transparent!important;
  background-image:linear-gradient(rgba(0,0,0,0) 60%,rgba(0,0,0,.5) calc(100% - 1px),hsla(212,100%,88%,.1) calc(100% - 1px)),linear-gradient(to right,rgba(0,0,0,0) 60%,rgba(0,0,0,.5) calc(100% - 1px),hsla(212,100%,88%,.1) calc(100% - 1px)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
  background-size:100% 100%,100% 100%,193px 93px!important;
  background-position:left -1px top 0,0 0,0 0!important;
  background-repeat:no-repeat,no-repeat,repeat!important;
  background-attachment:local,local,local!important;
}
scrollbar>scrollbarbutton{
  background:url("http://i.dreamflame.in/?i=ff/nn_sbars.png&v2&nl")!important;
  opacity:.5!important;
  transition:.4s ease-out,color .4s ease-out,background-image .4s ease-out,box-shadow .4s ease-out!important;
}
scrollbar>scrollbarbutton:hover{opacity:1!important}
scrollbar[orient="vertical"]>scrollbarbutton[type="decrement"]{background-position:1px 1px!important}
scrollbar[orient="vertical"]>scrollbarbutton[type="increment"]{background-position:1px -13px!important}
scrollbarbutton[type="decrement"]{background-position:-16px 2px!important}
scrollbarbutton[type="increment"]{background-position:-14px -14px!important}
