@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("folding.extremeoverclocking.com"), url-prefix("http://kakaostats.com/") {

* { background: none !important; color: inherit !important; }
html, option { background: #111 !important; color: #ccc !important; }
th { background: #222 !important; color: #09f !important; background-image: linear-gradient(rgba(255,255,255,0.1),rgba(125,125,125,0.1),rgba(0,0,0,0.2)) !important; }
.ad, .tablead, .divad { display: none !important; }

tr[bgcolor="#FFFFFF"], .ln { background: #1d1d1d !important; }
tr[bgcolor="#F7F7F7"], .ls, /* EOC */ .urlbar { background: #181818 !important; }
tr[bgcolor]:hover, .ln:hover, .ls:hover, .corpo > tbody > tr:hover, #nav ul, /* title */ .cab, /* JS tooltip */ #overDiv { background: #262626 !important; }

table[bgcolor="#C0C0C0"] { background: #444 !important; }
td[bgcolor="#FFFFFF"] { background: #1d1d1d !important; }
td[bgcolor="#EFEFEF"] { background: rgba(70,70,70,.1) !important; }
/* Presto fix */ #rndm:-o-prefocus, td[bgcolor="#EFEFEF"] { background: none !important; }
.corpo, .corpo th, .cab { border-color: #444 !important; }

a { color : #47b !important; }
.alt1, .alt1 a, .c1, .r { color: #f33 !important; }
.alt2, .alt2 a, .c2 { color: #f77 !important; }
.alt3, .alt3 a, .c3, font[color="#FF9900"] { color: #fa0 !important; }
.alt4, .alt4 a, .c4, .g, font[color="#008000"] { color: #2f4 !important; }
.alt5, .alt5 a, th a, .c5 { color: #09f !important; }
.alt6, .alt6 a, .c7 { color: #fff !important; }
.alt7, .alt7 a, .c8, .disabled { color: #999 !important; }
.alt8, .alt8 a, .c6 { color: #4b7 !important; }
.alt9, .alt9 a { color: #f5f !important; }
.alt10, .alt10 a { color: #800 !important; }

a:hover,
.alt1 a:hover,
.alt2 a:hover,
.alt3 a:hover,
.alt4 a:hover,
.alt5 a:hover,
.alt6 a:hover,
.alt7 a:hover,
.alt8 a:hover,
.alt9 a:hover
{ color : #f90 !important; }

/* ToS; utils/woebegonefurred.php */ span[style="color:#FFF;"] { color: transparent !important; }

img, input[type="image"] { opacity: .65; }
img:hover, input[type="image"]:hover { opacity: 1; }

::-moz-selection { background: #184880 !important; color: #fff !important; }
::selection { background: #184880 !important; color: #fff !important; }
tr[bgcolor]:hover ::-moz-selection { background: #fff !important; color: #184880 !important; }
tr[bgcolor]:hover ::selection { background: #fff !important; color: #184880 !important; }

input[type="submit"], input[type="button"], input[type="file"], input[type="reset"] { padding: 2px 6px !important; }
input[type="submit"], input[type="button"], input[type="file"], input[type="reset"], button
{ background: #111 !important; color: #fff !important; border: 1px solid #555 !important; border-radius: 4px !important;
background-image: linear-gradient(rgba(255,255,255,.2),rgba(125,125,125,.2),rgba(0,0,0,.3)) !important; }

input[type="submit"]:hover, input[type="button"]:hover, input[type="file"]:hover, input[type="reset"]:hover, button:hover
{ background-color: #000 !important; color: #4be !important; border-color: currentColor !important; }

button, input, select, textarea { -moz-appearance: none !important; }
select > button { background-color: transparent !important; border: none !important; }
input[type="radio"], input[type="checkbox"] { background: #222 !important; color: #4be !important; border-color: #999 !important; }

input, select, textarea, input[type="text"] { background: #181818 !important; color: #fff !important; padding: 2px !important; border: 1px solid #555 !important; }
input[type] { border-width: 1px !important; border-style: solid !important; }
textarea:hover, input:hover, select:hover, textarea:focus, input:focus, select:focus { border-color: #09f !important; }

}
