css:
@-moz-document domain(deviantart.com), domain(sta.sh) {

.username-with-symbol span.user-symbol {display: none!important}

.regular.username:before {content:"~"}
.banned.username:before  {content:"!"}
.username.premium:before{content:"\2a"}
.username.beta:before{content:"\42"}
.username.hell:before{content:"\68"}
.username.hell-beta:before{content:"\48"}
.username.senior:before{content:"\60"}
.username.volunteer:before{content:"\5e"}
.username.admin:before{content:"\24"}
.username.group:before{content:"g"}



.username:not(.regular):before {font-family:'usersymbol-icons';font-size:90%; font-weight: normal; letter-spacing:1px}
a.u.username:before {color:#304030;}

.username.super.group:before{color:rgba(216,108,42,0.8)}
.username.hell:before{color:rgba(216,108,42,0.8)}
.username.banned:not(.external):before {color:#304030; font-family:inherit; font-weight:inherit; font-size:90%;letter-spacing:2px;}

.cc-copy a:before{color:#97a2a0 !important}
div.rowuser .username:before {color:#90a193}
div.deviousness_text .username:before {color:#90a193}

.external:after{font-size:75%;}

}
