    .nav--inner, nav, .nav--subnav-menu, .nav--subnav-inner  {background: #a5a5a5 !important;}
    .nav.sticky   {background: #a5a5a5 !important;}


.sticky {position: relative !important}


      nav.nav .nav--subnav-items, .nav--inner, nav, .nav--subnav-menu, .nav--subnav-inner  {transition-delay: 1s !important; }     

                   /* Top Bar */


        .leaderboard--inner  { background: #e6e6e6 !important;}


        .leaderboard.with-bg    { background: #e6e6e6 !important;}
 
          .site-wrapper      { background: #eeeeee !important;}
   
 
          .article, .page-container  { background: #e6e6e6 !important;}


         .article-body--container, .article-body--container-main  { background: #e6e6e6 !important;}



         div.article-body--text, p.float-with-ad   { background: #e6e6e6 !important;}  


         p.float-with-ad    { background: #e6e6e6 !important;}



