css:
@-moz-document url-prefix("https://eksisozluk.com/") {
body {
    font-family: 'Segoe UI', sans-serif;
    font-size: .78em;
    background-color: #f1f1f2;
    color: #010101;
}

a {
    color: #2a62aa;
}

#topic h1 {
    font-size: 1.8em;
    margin: 10px 0;
  	text-transform: uppercase;
  	color:#08c;
}

html>body>header #top-bar {
    width: 100%;
    padding: 5px 20px;
}

#container {
    width: 100%;
    max-width: 100%;
    padding: 5px 20px;
}

#index-section {
}

.topic-list.partial>li>a {
    padding: 3px 0;
    line-height: 130%;
  	text-transform:none;
}

#content-body {
    width: auto;
	width: 100%;
    padding:0 20px;
  	margin:0 auto;
}

#content>#aside {
    display: none;
    visibility: hidden;
}

.ads {
    display: none;
    visibility: hidden;
    height: 0;
    width: 0;
}
}
