css:
body {
	width: 640px;
  	font-size: medium;
  	-webkit-font-smoothing: none;
  	font-smooth: never;
	margin: 20px 0 0 20px !important;
	padding: 0 !important;
}

*,
* *, 
* * * {
  	font-family: Arial, sans-serif !important;
  	line-height: 1.5 !important;
  	color: #000 !important;
	background: #FFF !important;
  	-webkit-box-shadow: 0 !important;
	text-shadow: 0 0 0 #C0C0C0 !important;
    border: 0 !important;
  	border-radius: 0 !important;
  	box-shadow: 0 0 0 #FFF !important;
  	max-width: 600px !important;
  	position: static !important;
  	float: none !important;
  	margin: 0 !important;
  	padding: 0 !important;
  	text-align: left !important;
  	list-style-image: none !important;
}
div,
li { width: 100% !important; }
li { display: list-item !important; }

a, a * { color: #000 !important; }
a { text-decoration: underline !important; padding: 0 !important; }
a:focus, a:focus *,
a:hover, a:hover * { color: #000 !important; }
a:active, a:active * { color: #000 !important; }
a:visited, a:visited * { color: #000 !important; }

a img { border: 2px solid blue !important; }
a:hover img,
a:focus img { border-color: blue !important; }
a:active img { border-color: red !important; }
a:visited img { border-color: purple !important; }

h1, h2, h3 { font-weight: bold !important; }
h1 { font-size: 2.5em !important; }
h2 { font-size: 2em !important; }

table{ border-collapse: separate; border-spacing: 1px 1px; border: 2px inset #C0C0C0 }

button, textarea,
input, select { display: inline-block }
textarea, input { background: #FFF !important; border: 1px solid black !important; }
button, input[type="submit"], input[type="button"], input[type="image"] { background: #C0C0C0 !important; border: 2px outset #CCC !important; }
