@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pikabu.ru") {

html>body>a,
html>body>div:nth-of-type(4)>a,
html>body>div:nth-of-type(3)>a,
html>body>div:nth-of-type(5)>table>tbody>tr>td:nth-of-type(2)>span>div:nth-of-type(4)>a,
html>body>div:nth-of-type(5)>table>tbody>tr>td:nth-of-type(2)>span>table:nth-of-type(2)>tbody>tr>td>center,
html>body>div:nth-of-type(5)>table>tbody>tr>td:nth-of-type(2)>span>div:nth-of-type(6)>a {
display: none;
}

html>body>div:nth-of-type(5)>table>tbody>tr>td:nth-of-type(2)>span>div:nth-of-type(4)>noindex>table>tbody>tr>td,
html>body>div:nth-of-type(5)>table>tbody>tr>td:nth-of-type(2)>span>div:nth-of-type(6)>table>tbody>tr>td {
border-top: 0 !important;
}
html>body>div:nth-of-type(4)>div, html>body>div:nth-of-type(3)>div {
margin-top: 65px !important;
}

}
