@-moz-document domain('npo-iasia.org') {
#container-inner {width: auto !important; padding: 0 !important;}

/*--- header ---*/
#header-inner,
#content-inner, #main-navigation,
.entry-asset .asset-meta,
.comment,
#footer-content,
.asset-body
 {height: auto !important}
#header-inner,
#main-navigation-inner,
#content-inner,
#footer-inner,
#header-content
 {width: auto !important;}

/*--- navigation bar ---*/
.widget-search {position: relative !important; top: 0 !important;}

/*--- multi columns topics ---*/
#content {margin: 0 !important;}
.layout-wm #alpha {width: 100% !important; margin: 0 -270px 0 0 !important;}
.layout-wm #alpha-inner {width: auto !important; margin: 0 270px 0 0 !important;}

/*--- footer ---*/
#footer {margin: 0 !important;}

}
