css:
@-moz-document url-prefix("http://tf2r.com/") {
body {
background: url(http://i.imgur.com/NejXjlf.png) fixed !important;
}

#header {
background: rgba(0,0,0,0.7) !important;
height:107px !important;
box-shadow: 0px 0px 15px #fff !important;
margin-bottom: 15px !important;
}

#member_holder {
background-color:#000 !important;
border: 1px solid #fff !important;
box-shadow: 0px 0px 7px #fff !important;
}

#nav_holder, #content {
border: 1px solid #fff !important;
background: rgba(0,0,0,0.7) !important;
}

#nav_holder {
border-bottom:0px !important;
}

#content {
border-top:0px !important; 
margin-bottom:15px !important;
}

.nbg, .text_holder, .newfeed, .raffle_infomation, .rafrep{
background: rgba(0,0,0,0.7) !important;
border: 1px solid #fff !important;
}
  
.userfeedpost {
background: rgba(0,0,0,0.7) !important;
}

div.pentry, input, option, textarea, select {
border: 1px solid #fff !important;
background: #000 !important;
}  
  
.mNeut {
box-shadow: 0px 0px 0px #fff !important;
}

.notif {
color:#ccc !important;
}
.ntifnew {
margin: 2px 15px 0px 15px !important;
}

.lev0 {
background: rgba(0,0,0,0.7) !important;
}
.pubrcont {
background: url(http://i.imgur.com/Q4S8QPZ.png) !important;
}
  
.pubrhead, #users, .tbg{
background: #000 !important; 
}

.pubrhead-arrow {
border-color: #000 transparent transparent transparent !important;
border-style: solid !important;
border-width: 10px !important;
}

.pubrarro {
border-color: transparent transparent transparent #000 !important;
}

.mSuc {
margin: 0px auto 0px !important;
}

::selection {
color:#fff;
background-color:red;
}
}
