css:
/*Pixivプルダウンメニュー展開*/
@-moz-document domain("www.pixiv.net") {
._type-slim-header ._header .notification-container li a {  
    padding-bottom: 0px !important;
    padding-right: 3px !important;
    padding-left: 3px !important;
    }

A[href="http://www.pixiv.net/stacc/?mode=unify"],
A[href="/group/group_list.php"],
A[href="/stacc/?mode=unify"],
._type-slim-header ._header .category-container > .current,
._type-slim-header ._header .user-container{
    display: none !important;
}
._type-slim-header ._header .title {
    margin-right:90px!important;
    }
    
._type-slim-header ._header .profile-container {
    border:none !important;
    right: -83px !important;
}
._type-slim-header ._header .navigation-list a  {
    float: left !important;   
    padding: 0px 8px !important;
    }
._type-slim-header ._header .profile-container .navigation-list  {
    display: block !important;
    width: 700px !important;
    border:none !important;
    position:relative !important;
    top: 20px !important;
    }
._type-slim-header ._header .navigation-list ul + ul {
    border: none !important;   
    padding: 0px !important;
    }
._type-slim-header ._header .navigation-list ul  {
    padding: 0px !important; 
    }
._type-slim-header ._header .navigation-list a:before {content:"● ";}

._type-slim-header ._header .profile-container:hover,
._type-slim-header ._header .title:hover {
    background: none !important;   
    }

._type-slim-header ._header .notification-container {
    border: none !important;   
    padding: 0px !important;
    left: 483px !important;
    top: -23px !important;
    }
    
._type-slim-header ._header .search-container  {
    border:none !important;
    right: 0px !important;
    padding: 0px !important;
    height: 28px !important;
    top: 14px !important;
    }
}
