css:
/* AGENT_SHEET */
/* tekphreak.com/craiglist.org/css/ */
@-moz-document domain(craigslist.org) {
html, body {background-color: #000000;color: #00ff00;}
html frameset frame#L div.fbod tbody tr td a b {color: white;}
html frameset frame#R div.fbod tbody tr td a b {color: white; background-color:black;}
/* the text-transform:lowercase; makes the ALL CAPS attention whores behave */
html body a {color: #00ff00;}
table.fbod.threads tbody tr td font {color: #00ff00;}
table.fbod.threads tbody tr td a {color: #00ff00; text-decoration: none; text-transform:lowercase;}
table.fbod.threads tbody tr td a b:hover {background-color: #7f0000;color: white;}
table.fbod.threads tbody tr td a:visited {color: #4c0000;}
table.fbod.threads tbody tr td font:after { display:none;}
/* this is the color of YOUR craigslist handle */
table.fbod.threads tbody tr td span.hnd.own {color: #ff0000 !important; text-decoration: underline;}
/* this is the color of the handle of everyone but you */ 
table.fbod.threads tbody tr td span.hnd {color: #ffffff;}
table.fbod.threads tbody tr td span.hnd.anon {color: #ffffff;}
table.fbod.threads tbody tr td span.hnd.anon span {color: #ffffff;}
table.fbod.threads tbody tr td span.hnd span.S {color: #ffffff;}
/* this above section will make it easier to see handles */
body a.pln {color: #00ff00;} 
span {color: #00ff00;}
fieldset {background-color: #000000; }
legend {color: #00ff00; background-color: #000000;}
b {font-weight: bold;color: #00ff00;}
input[type="submit"] {border:1px solid #00FF00;background-color: #000000;color:#00DD00;}
input[type="checkbox"] {display:none;}
textarea {border:1px solid #00FF00;background-color: #000000;color: #00FF00;}
select, option [type="submit"] {border:1px solid #00FF00;background-color: #000000;color:#00DD00;}
frame#R a.pln {display:none;}
/********************************************************************/
/* this bit will dim all images, silently muting the image spammers */
/*                                                                  */
/*   simply mouse over the images to display them dynamically       */
/*                                                                  */
/********************************************************************/
img {background-color: #00FF00;opacity: 0.05;border:1px dashed #00FF00 !important;background: url("http://i.imgur.com/rvlVuiy.gif") transparent no-repeat; }
img:hover {opacity: 1.0;border:1px solid #00FF00 !important;}
}
