css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("sourceforge.jp"),
domain("sourceforge.net") {
  /* 上部広告を非表示にします */
  #ad-leaderboard,
  #banner-ads
  { display: none; }

  /* 右上部広告を非表示にします */
  div[id^='google_ads_iframe'] { display: none; }

  /* 下部広告を非表示にします */
  #ad_dev_footer { display: none; }
  
  /* その他広告 */
  .ads,
  .jobs-widget,
  *[id^="google_ads"]
  { display: none !important; }
}
