css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("softonic.jp") {
  /* 上部広告を非表示にします */
  /* hide top ads */
  div.leaderboard
  { display: none !important; }

  /* 右上部広告を非表示にします */
  /* hide right top ads */
  .show_banner_medium_rectangle
  { display: none !important; }

  /* 下部広告を非表示にします */
  /* hide bottom ads */
  .bottombanner
  { display: none !important; }

  /* コメント欄を非表示にします */
  /* hide question area */
  #ask_question
  { display: none !important; }
}
