css:
@-moz-document regexp("https://progtest.fit.cvut.cz/.*Act=A.*") {
/* Řešení */
.outBox textarea.stdDis.fw { height: 5em; }

/* Učitelské hodnocení */
.outBox textarea.std.fw { height: 3em; }

/* Odevzdané řešení */
.outBox .ltbSepCell,.rtbSepCell { display: none; }

/* Jméno studenta */
.outBox tr:nth-of-type(1) { font-size: 0.5em; }

/* Zadání úlohy */
.outBox tr:nth-of-type(3) td { background-color: white; font-family: sans-serif; }

/* Hodnocení automatem */
.outBox tr:nth-of-type(5) { font-size: 0.75em; }

/* Podbarvení selectu */
.outBox tr:nth-of-type(8) select option:nth-of-type(1) { background-color: red; }
.outBox tr:nth-of-type(8) select option:nth-of-type(2) { background-color: yellow; }
.outBox tr:nth-of-type(8) select option:nth-of-type(3) { background-color: olive; }
.outBox tr:nth-of-type(8) select { background-color: white; }

/* Opisování */
.outBox tr:nth-of-type(11),tr:nth-of-type(12)  { display: none; }

/* Uložit */
input.updButton[name="U"] { background-color: olive; }
}
