css:
@-moz-document regexp('https?://\\w+\\.bing\\..*') {

/* all the major elements */
body {
	font-family: Segoe UI, Lucida Mac, Lucida Grande, Lucida, Calibri, sans-serif; 
	background-color: white !important;
	color: #6f7a7a !important;
}

/* link coloring */
a, div.kno-lc {
	color: #268bd2 !important;
	/* blue */
}
a:visited {
	color: #ff7d66 !important; /* light orange */
	
	/*color: #d33682 !important;*/ /* magenta */
	
}
a:hover {
	color: #ff5233 /*#ff330f*/ !important;
	/* orange */
	
}

/* fix indentation of search results */
#sw_main { 
  padding: 0px 0px 1.54em 5px;
}

/* result page text excerpt */
.sa_mc {
	color: #6f7a7a !important;
}

/* url */
.sb_meta cite {
	color: #4fa244 !important;
}

}
