@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goo.ne.jp") {

  /**** 広告を非表示にします ****/

  /* ページ最上部ロゴ～検索バー間バナー広告 */
  #gooad-long
  { display: none !important; }

  /* 質問上部広告 */
  .pr-unit
  { display: none !important; }

  /* ベストアンサー～全回答間広告 */
  /* 全回答間広告 */
  .ba
  { display: none !important; }

  /* 全回答下部広告 */
  #xlisting-ad-1
  { display: none !important; }

  /* 注目の記事下部広告 */
  #connection_area+div.clearfix
  { display: none !important; }

  /* 最下部 goo版IE広告を非表示にします */
  .pr
  { display: none !important; }

  /* 右部メニュー内広告（2か所） */
  #gooad_rect
  { display: none !important; }

  /* 各種広告エリアを非表示にします */
  *[id^="gooad-badge"],
  *[id^="gooad-mkkbadge"]
  { display: none !important; }

  /* goo路線 右メニュー部 PR広告を非表示にします */
  #areamatch_20006_70004_300
  { display: none !important; }

  /* goo路線 右メニュー部 リスト広告を非表示にします */
  .googlead
  { display: none !important; }

  /****************************/
  /**** 表示を切り詰めます ****/

  /* Q&Aの参照履歴 の高さ固定を解除 */
  .qa_past_list
  { height: auto !important; }

}
