css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yidianzixun.com") {
    *{
        font-family: MSYH !important;
    }
    
    a{color: #C7EDFF !important; }
    span{color: #C7EDCC !important; }
    ul{background-color: #555555 !important; }
    p{
        color: #C7EDCC !important; 
        font-size: 1.1em !important;
    }
    h1{font-size: 1.8em !important; background-color: transparent !important; color: #C7EDCC !important; }
    
    div.content-bd{color: #C7EDCC !important; }
    div.main{background-color: #151515 !important; }
    div.comments{background-color: #151515 !important; }
    div.section.section-hotchannel{background-color: #555555 !important; }
    div.top-bar-header{background-color: #151515 !important; }
    div#box{background-color: #151515 !important; }
    div.wrapper{background-color: #151515 !important; }
    div.a-doc{background-color: #252525 !important; color: #C7EDCC !important; }
    div.top-bar{background-color: #151515 !important; color: #C7EDCC !important; }
    div#doc-mask{background-color: #000000 !important; color: #C7EDCC !important; }
    div#last-row{background-color: #555555 !important; }
    div[id*="chn-"]{background-color: #555555 !important; }
    
    a.a-lk{font-size: 3em !important;}
    a[style^="background-color: rgb"]{background-color: #555555 !important;}
    a.anim{background-color: #555555 !important; }
    .a-tag a{background: #C7EDFF !important;}
    .doc-content a{font-size: 1em !important;}
    
   
    h3 > a {font-size: 130% !important; }
    
    h2 {color: #C7EDCC !important;}
    
    span.article-chn{background: #888888 !important;}
    span.article-like{background: #ff5555 !important;}
    span.article-dislike{background: #5555ff !important;}
    
    article{color: #C7EDCC !important;} 
}
