css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("songmeanings.com") {

#wrapper, #main div, .tabset-more>li>a, .tabset-more {
 background-color: black !important;
color: #ccc;
}

#sidebar .block-heading>h3, #sidebar .block-heading-blue>h3, .heading>h2, .tabset-more a, #albumlisting a {
color: #ccc !important;
}

.holder, .main-holder, #content .block .tabset-more, #content .block .tabset-more a {
background-image: none !important;
}

.tabset-more a {
border:none !important;
}

.marked {
background-color: #222 !important;
}


.block-heading-blue {
  -moz-border-bottom-colors: none;
  -moz-border-left-colors: none;
  -moz-border-right-colors: none;
  -moz-border-top-colors: none;
  background: none repeat scroll 0 0 #F3F3F3;
  border-color: #DCDDDE !important;
  border-image: none;
  border-radius: 3px 3px 0 0;
  border-style: solid;
  border-width: 1px 1px 0 !important;
  padding: 9px 20px;
  position: relative;
  text-align: left;
  z-index: 2;
}

#content>div {
float:none !important;
}

.block-feed {
width: 98% !important;
}

.lyric-box {
color: #ff0 !important;
background-color: #123 !important;
}

.lyric-box a {
background-color: #444 !important;
border-color: #222 !important;
}

#textblock {
 color:yellow !important;
text-align: center;
font-size: large !important;
line-height: 22px;
}

.song-info>li>a {
background-image: none !important;
padding: 0px 8px !important;
}

}
