css:
@-moz-document domain("juick.com") {

    /* {{{ general stuff */
    html, input {
       font-size: 100% !important;
    }

    body, header {
       width: auto !important;
    }
    
    .msg-cont {
       width: auto !important;
       padding: 0px !important;
    }

    #wrapper {
        width: auto;
    }
    /* }}} general stuff */
    
    /* {{{ header */
    /* let's not be hostile to branding */
    /* #logo { display: none; } */
    #logo a { background-size: 30px 16px !important; }
    #logo { height: 16px !important; }
    
    #search {
       margin:0 !important;
       padding:0 !important;
    }
    
    /* no extra space around header links */
    nav li {
       margin:0 !important;
    }
    
    /* no space under header */
    #content {
       width: auto !important;
       margin: auto !important;
       float: none !important;
    }
    
    /* post margins */
    .msg {
        margin: 1px 0px 2px  !important;
        min-height: 50px !important;
    }

    /* }}} header */

    .title2 {
        padding: 0px !important;
        margin: 0px !important;
    }
    
    article {
        margin-bottom: 5px !important;
    }
    
    .msg-cont {
        margin-bottom: 5px !important;
    }
    
    /* {{{ comment  */
    .msg-header {
        display: inline !important;
    }

    .msg-txt {
        margin: 0px !important;
        display: inline !important;
    }
    
    .msg-links {
       margin: 0px !important;
    }
    /* }}} comment  */
}
