css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.southparkstudios.com/episodes/") {
  #header {
    border: 3px solid red;
    display: none !important;
  }

  #episodewrapper {
    position: absolute;
    left: 0px;
    width: 100%;
  }

  #flashcontent {
    position: relative;
    top: -10px;
    left: -180px;
    width: 100%;
  }

  .content_carouselwrap div {
    position: relative;
    left: -130px;
  }

  .content_carouselwrap h4 {
    display: none !important;
  }
}
