css:
@-moz-document url-prefix("http://www.bg-mamma.com/index.php?topic=") {

.wrap-inside
{
  width:1000px;
}

.post
{
  width:820px; 
  max-width:820px; 
}

.topicp
{
  width:820px !important;
}

.th-row-l
{
  display:inline-block;
  width:140px;
  height:auto;
  vertical-align:top;
}
.th-row-l .all
{
  background:#FAFCFF;

}
  
.all-post-content
{
  display:inline-block;
  background:none;
}

.post-row .all .right-panel a
{
  font-size:10px;
}

.post-row .all .user-info span
{
  font-size:11px;
}

.post-row .all
{
  border-bottom: 0 none;
}

.first-post-content, .power-buttons, .post-bottom
{
  border-bottom: 0 none;
  padding: 0;
  margin-bottom: 0;
}

.replypostbtn
{
  padding-top:10px;
}

.re-topic a
{
  border-bottom:0 none;
  border-top:0 none;
  background-color:#E6E6E6;
}

.rank, .p-info
{
  padding-top:3px;
  color:#999;
}


/* Removing of right pane */

.sidebar
{
  display:none;
}

.share-sidebar
{
  display:none;
}

.close-but
{
  display:none;
}

.content-inside
{
  margin:0;
}
}
