css:
@-moz-document domain("tweetdeck.twitter.com") {

  /*********************************************
   *  this style based on following style, written by dalboz and Optimized for tweetdeck Persian/Farsi users
      http://userstyles.org/styles/78667/tweetdeck-metro-light-with-column-colors
   *********************************************/

  /* fonts & white bg colors */
  body, #container {
      font-family: 'Segoe UI', 'Verdana', 'Arial', 'Helvetica', sans-serif;
      text-shadow: none;
  }
  textarea, p{
      font-family: "Droid Sans", "iranian sans", tahoma !important;
      direction:rtl;
      text-align:right;
  }
}
