css:
@-moz-document domain("facebook.com") {
	*[dir=rtl],
	html[dir=rtl] > body,
	div[dir=rtl],
	span[dir=rtl],
	.webMessengerMessageGroup ._38 p,
	html[dir=rtl] *	{
		font-family: "Arabic Typesetting", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Jameel Noori Nastaleeq !important;
		font-size: 135%;
		line-height: 155%;
	}
}

@-moz-document domain("twitter.com") {
    *[dir=rtl],
    div[dir=rtl],
    span[dir=rtl],
    .tweet-text-rtl {
		font-family: "Arabic Typesetting", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Jameel Noori Nastaleeq !important;
		font-size: 125%;
		line-height: 145%;
    }
}

@-moz-document domain("ur.wikipedia.org") {
    *[dir=rtl],
    html[dir=rtl] > body,
    div[dir=rtl],
    span[dir=rtl],
    html[dir=rtl] * {
		font-family: "Arabic Typesetting", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Jameel Noori Nastaleeq !important;
		font-size: 105%;
    }
}

@-moz-document domain("google.com"), domain("google.com.pk") {
	*[dir=rtl],
	html[dir=rtl] > body,
	div[dir=rtl],
	span[dir=rtl],
	.direction_rtl
	.rtl,
	html[dir=rtl] * {
		font-family: "Arabic Typesetting", Alvi Lahori Nastaleeq, Alvi Nastaleeq, Jameel Noori Nastaleeq !important;
		font-size: 200%;
	}
}

@-moz-document domain("mail.google.com") {
.ii.gt td, .ii.gt input, .ii.gt textarea, .ii.gt select, .ii.gt div, .ii.gt span {
    font-family: "Arabic Typesetting", Jameel Noori Nastaleeq !important;
    font-size: 20px !important;
}
}
