css:
@-moz-document domain("forum.xda-developers.com") {
body * 
	{color: #224 !important; font-weight: normal !important; font-size:1em!important; 
	line-height: 1.9em; }
body a, body a:visited, body a font, body a:visited font { color:#0000f8 !important; font-family: 'Helvetica Neue', Helvetica, Sans-serif; }
body a:hover, body a:visited:hover, body a:hover font, body a:visited:hover font  { color: RED !important; }
tr#xda_titleHeader {background: url("http://i.imgur.com/ItECAkK.jpg") repeat-x scroll 0 0 !important ;}
div[id*='div-gpt-ad'] {display:none;}
div#posts table.tborder td.postContent {padding: 20px 40px 20px 100px;}
img.inlineimg {width:1em; height: 1em; margin: 0 8px; opacity: 0.3;}

font[face=Impact], font[face='Comic Sans MS'], font[face='Franklin Gothic Medium'],
font[face='Lucida Sans Unicode']
	{display: block; font-family: helvetica, sans-serif; background: #ffe; 
  	border:1px solid #ff8; padding: 20px; }

td.postContentHead div.postDate,
div#posts table.tborder td.posterInfoHead,
div#posts table.postHeader td.postTitle, 
td.submitAsTip,
div#posts table.tborder td.postContentHead,
table.tborder td.posterInfoFoot,
table.tborder td.postContentFoot
{
  background-color: #fff; 
  font-weight: bold!important; 
}

div#posts table.tborder td.postContentHead {
  padding-left: 100px;
}
}
