css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("listenonrepeat.com") {
    body {
        background-color: #111111 !important;
    }
    
    a {
        color: #aaaaaa !important;
    }
    
    #header {
        border-bottom-color: #222222 !important;
    }
    
    #content {
        color: #ffffff !important;
    }
    
    #mainarea #title {
        color: #cccccc !important;
    }
    #mainarea #embed-right {
        color: #cccccc !important;
    }
    
    .arrow_box:after {
        border-right-color: #000000 !important;
    }
    .arrow_box:before {
        border-right-color: #333333 !important;
    }
    
    .arrow_box {
        background-color: #000000 !important;
        border-color: #333333 !important;
    }
    
    .bar {
        border-bottom-color: #222222 !important;
    }
    
    .box {
        background-color: #000000 !important;
        color: #c3c4c4 !important;
        border-color: #333333 !important;
    }
    
    /*.box_label {
        background-color: #35ced3 !important;
        color: #000000 !important;
    }*/
    
    .box_title a {
        color: #ca312c !important; /* negative value: #35ced3 */
    }
    
    .videorow:hover {
        background-color: rgba(24, 25, 25, 0.63) !important;
        border-right-color: #303030 !important;
    }
    
    .videorow img {
        border-color: #000000 !important;
    }
    
    .videorow_info a {
        color: #c3c4c4 !important;
    }
    
    .videorow_meta_large {
        color: #c3c4c4 !important;
    }
    
    .box_showmore {
        color: #aaaaaa !important;
        background-color: rgba(24, 25, 25, 0.63) !important;
    }
    .box_showmore:hover {
        background-color: rgba(24, 25, 25, 0.2) !important;
    }
    
    .leaderboard a {
        color: #c3c4c4 !important;
    }
    .leaderboard a:hover {
        color: #ffffff !important;
    }
    
    .footer a {
        color: #777777 !important;
    }
    #footer-links a:hover {
        color: #aaaaaa !important;
    }
    
    /* Home */
    #article img {
        border-color: #000000 !important;
    }
    
    /* Terms, Privacy */
    #title {
        color: #cccccc !important;
    }
}
