css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kuai.xunlei.com") {

/* files */

.c_2 a.file_name {
    font-size: 0;
}
.c_2 a.file_name:after {
    font-size: 13px;
    content: attr(title);
}

span[class="c4"] {
    position: absolute;
    right: 90px;
    float: right !important;
}
span[class="c4 status"] {
    width: 100px !important;
    position: absolute;
    right: 0;
}

.adv_area, .file_right, .advl,
.hot_list, .high_btn {
    display: none !important;
}
.file_left, .file_src, .file_src li {
    width: 100% !important;
    height: 100% !important;
}
.c_2, .c_2 a {
    width: auto !important;
}


/* folder */

.file_left, .downLoad_area {
    width: 100% !important;
}
.file_w, .file_list {
    height: 100% !important;
}

}
