css:
@-moz-document url-prefix("http://baymirror.com/"), regexp("(http|https)://([a-z0-9]+[.])*(thepiratebay|tpb).*") {
/* ==========================================================================
   cleaning some relatively useless things
   ========================================================================== */

/* remove unnecessary info + ads */
footer, #howdl, #sky-right, #sky-banner, .ad, iframe, #footer, #divExoLayerWrapper, #fbanners {
  display: none !important; 
}

/* remove ads blank space */
#main-content { margin-left: 0; }

/* ==========================================================================
   clear style
   ========================================================================== */

/* skeleton */
#header {
  width: 825px;
  margin: auto;
  padding-bottom: 10px;
}

#fp {
  font-size: 15.5px;
  position: absolute !important;
  top: 0; bottom: 0; right: 0; left: 0;
  height: 640px;
  margin: auto !important;
}

#categoriesTable {
  width: 65%;
}
#browseContainer dt {
  border-radius: 5px;
  padding: 15px;
  background: #F6F1EE;
  font-weight: normal;
}

/* standardizing fonts */
body, input, select, .nfo pre {
  font-family: Arial !important;
  line-height: 1.5em;
}

/* form */
input {
  font-size: 18px;
  border-radius: 3px;
  background: #F6F1EE !important;
}

input[name="q"] {
  font-size: 20px;
  padding: 8px !important;
  width: 511px !important;
}

#fp input[name="q"] { width: 582px !important; }

input, select { border: 1px solid #ccc; }

input[type="submit"] {
  padding: 9.5px;
  background: #eee;
}

input[title="Pirate Search"] { color: #A4724D;}

/* highlighting page numbers */
#SearchResults div[align="center"] {
  margin-top: 20px;
  font-size: 17px;
}

#SearchResults div[align="center"] a {
  border: 0;
  margin-right: 4px;
  font-weight: bold;
}

/* random content */
a[title="Preferences"]:before {
  content: "Login ";
  display: block;
}

.download { font-size: 20px; }

#chb label { margin-right: 10px; }

h2 { padding: 10px; }

.comment {
  padding: 10px;
  font-size: 12px;
}

.download { text-align: center; }

.download a {
  padding: 8px 0 10px 35px !important;
  background-image: url("https://cdn1.iconfinder.com/data/icons/PixeloPhilia_2/PNG/magnet.png") !important;
  margin: 5px;
  font-size: 0.8em;
  display: inline-block;
}

form span {
  margin-top: 3px;
  display: inline-block;
}

form span a {
  font-size: 11px;
  line-height: 1.4em;
}

.nfo pre { font-size: 13px !important; }

/* seeders section */
td:nth-child(3) {
  font-size: 13px;
  font-weight: bold;
}

#header a, #fp a { color: #A4724D; }

a {
  color: #684a34 !important;
  border: 0 !important;
}

a:hover { color: #A4724D !important; }

#detailsframe #title { font-size: 15px; }

.nfo pre { font-size: 14px; }

/* torrent's title */
.detName a { font-weight: bold; }
}
