css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://mush.twinoid.com/"), url("http://mush.twinoid.com/#") {
    
    html {
        width: 480px !important;
        background: none #0f0f43 !important;
        overflow-x: hidden !important;
    }

    /* Background removal; for performance */
    #container_bg,
    #container_bgleft,
    #container_bottomright,
    #container_bottomleft,
    #container_topright,
    #maincontainer,
    .sheetpabg, .sheetpa,
    .alarm_on, .alarm_right_on, .alarm_bg_on,
    .itemactionsbg, .itemtitle, .itemactionstop {
        background: none transparent !important;
    }
    
    /* Things removal; for performance */
    .tid_footer, .tid_shortcuts, .tid_shadow {
        display: none !important;
    }
    
    
    /* Layout Fix */

    #container_inside {
        padding: 10px !important;
        /* Effective Width: 460px */
    }

    .mxhead > *, .tid_topBar {
        width: 480px !important;
    }
    
    .alarm {
        width: 134px !important;
        height: 36px !important;
        padding: 8px !important;
    }
    
    .genstatus > tbody > tr > td {
        background: #242450 !important;
    }
    
    #mush_content > .inter > tbody > tr > td {
        float: left !important;
        clear: left !important;
        padding: 20px 0 !important;
        margin-bottom: 5px !important;
        background: #242450 !important;
    }

    #char_col { width: 100% !important; }
    #char_col > div { float: left !important; }
    #char_col > div:last-child { margin-left: 32px !important; }
    
    #room_col { width: 100% !important; }
    #room_col > div { width: 424px !important; margin-left: 18px !important; }
    
    #chat_col { width: 100% !important; }
    
    #cdInventory, #cdInventory * {
        visibility: visible !important;
        display: inherit !important;
    }
    
    #cdInventory {
        position: relative !important;
        top: 120px !important;
    }
    
    .bgmachine { margin-top: -186px !important; margin-left: 341px !important; }
    
}
