css:
@-moz-document domain(www.ex.ua) {

body {
font-family: tahoma, sans-serif, verdana;
font-size: 9pt;
color: #bcf;
background-color: #111111;
margin: 0;
height: 100%;
}

a:link {
color: #99f;
text-decoration: none;
}

a:hover {
color: #33f;
text-decoration: none;
}

a:visited {
color: #99f;
text-decoration: none;
}

a:visited:hover {
color: #33f;
text-decoration: none;
}

.small a:link {
color: #99f;
text-decoration: none;
}

.small a:hover {
color: #33f;
text-decoration: none;
}

.small a:visited {
color: #99f;
text-decoration: none;
}

.small a:visited:hover {
color: #33f;
text-decoration: none;
}

.include_0 tr td {
border: 1px solid #023;
padding: 16px;
width: 25%;
box-shadow: 0 0 10px 1px #bcf;
}

.r_button a {
border: 1px solid #bcf;
background-color: #012;
color: #99f;
box-shadow: 0 0 10px 1px #bcf;
}

.r_button a:hover {
border: 1px solid #33f;
background-color: #012;
color: #99f;
box-shadow: 0 0 10px 1px #33f;
}

.r_button_small a {
font-size: 8pt;
border: 1px solid #bcf;
background-color: #012;
color: #99f;
box-shadow: 0 0 10px 1px #bcf;
}

.r_button_small a:hover {
font-size: 8pt;
border: 1px solid #33f;
background-color: #012;
color: #99f;
box-shadow: 0 0 10px 1px #33f;
}

.small {
font-size: 8pt;
color: #bcf;
}
    
select {
color: #bcf;
border-color: #bcf;
background-color: #012;
box-shadow: 0 0 10px 1px #bcf;
}
    
img {
border: 0px solid #bcf;
background-color: #012;
box-shadow: 0 0 10px 1px #bcf;
}

.menu {
border-bottom: solid 1px #bcf;
background-color: #023;
}

input {
font-size: 9pt;
color: #bcf;
background-color: #012;
border: 1px solid #bcf;
box-shadow: 0 0 10px 1px #bcf;
}

}
