css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("onlinepj.com"), domain("www.onlinepj.com") {


/* body */


body { background:linear-gradient(to bottom, darkgrey 0%, lightgrey 44%, darkgrey 100%) repeat scroll 0% 0% transparent; }
.fixed { background:none; }
.fixed img { padding-left:3px; }
.fixed { width: 100% }
.fixed > a:first-child { margin-left:17%}
.inner { width:98% }
.container { margin-bottom:0px; border-bottom:none; }
.top_bar { background:lightseagreen; }
.menufixed { top:7%; }

/* Navigation */

.menu > li > a:hover, .menu > li > a.active { background:linear-gradient(to bottom, #167C8D 0%, lightseagreen 100%) repeat scroll 0% 0% transparent; box-shadow:none; }
.slidingDiv { position: absolute; z-index: 99; top:0; }

/* body : main */

.main { width : 69% }
.box_outer { border:none; }
.news_box { border:none; }
.news_box .news_box_heading .nb_dots { background:none; }
.news_box .news_box_heading { background:lightseagreen; }
.news_box .news_box_heading h2 { padding:0px; background:lightseagreen; }
.lates_video_news { border:none; }
.lates_video_news .widget_title { background:lightseagreen; border:none;height:45px; padding:10px 0px 0px 20px; font-size:18px; font-weight:700; color:#2F2F2F; }
.cat_article { border:none; }
.widget iframe{ width:280px; height:400px; overflow:auto; }

.acc_content > p { line-height:15px; padding:8px 0px; }
.acc_content > p:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }

.scrollable { width:100% }
.scroll_items { width:100% }
.four_items { width:100%; left:24px; }
.lates_video_item { width:22% }
.lates_video_item:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }
.custom_tabs2_content > p {  }
.custom_tabs2_content > p:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }
.current:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }
.more_news > p:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }

/* body : sidebar */

.sidebar { width: 29%;}
.sidebar .widget .widget_title { background:lightseagreen; }
.tabbed_widget ul.tabbed_nav li a.current {background:lightseagreen;}

.cat_article h2 { background:lightseagreen; }

.sidebar .widget .widget_title { height: 50px; line-height: 50px; }
.sidebar .widget { border:none; }
.box_outer { border:none; }
.widget_title { color:#40454D }

#ks-diyfan ul li { width:26%; }
#ks-diyfan ul li:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }

.blog_posts_widget li:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }

.sidebar .widget iframe { margin-left:60px } 

/* body : footer */

.box_head:hover { background:lightseagreen; -webkit-transition:color 1s; transition:color 1s; }
.box_note { border :none; }
}
