@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iitv.info") {
body, .polecamy, .player_li, div.add_new_link, .latest_title, .serial_info  {background-color: black !important;}
header {background: black !important;}
#header nav ul li a {color: white !important;}
#colOne {background: none !important;background-color: black !important;}
#colOne li {border-top: 1px solid black !important;}
#colTwo, #logo, #search, #menu, .bottom {background: none !important;}
#header {background: none !important; border-bottom: none !important;}
#footer, #FB_SERVER_IFRAME, .time_info, .time_info_mvideo, .play_rek_mvideo, div.reklamy_embed, #prox_rek, .rek_pp, .scs_ad, #fb_2, div.player_cap_caption, .top_link_ad, .social_box, div.player_ad_time, div.reklamy_embed, a[href^="http://megulo.com"], .top_link_ad, div#video_reklamy, div.reklama-content-1, a[href~="http://premium.iitv.info"]:not([title="premium.iitv.info"]) {display:none !important;}
div#ajax_player_load > div.player_cap > div.play {display: block !important;}
.serial_episode, .serial_tit a {color:#909090 !important}
#menu ul li {background-color: #202020 !important}
.latest_info a, .release, #serial_desc {color:white !important;}
.serial_menu a:first-child, div#episode_menu ul > li > a > div {color:lime !important;}
#menu form {background-color: black !important; border-top: none !important;}
div.embed.embj,div.player_box_hid,div.embed {display: block !important;}
form.login > input {background-color: grey !important; border: none !important;}


}

