css:
@-moz-document domain("youtube.com") {
.watch-medium .player-height { height: 600px; }
.watch-medium .player-width { width: 1000px; }
.html5-video-player .video-click-tracking, .html5-video-player .video-stream { width: 100% !important; height: 100% !important; }
.site-center-aligned #player.watch-large, .site-center-aligned #player.watch-medium { margin-bottom: 0; }
.html5-main-video, .html5-video-content { left: 0 !important; width: 100% !important; height: 100% !important; }
video { left: 0 !important; }
}
