css:
@-moz-document domain("liveleak.com") {
#footer {
    background-color: #000000;
    border-color: #000000;
    color: #d6d6d6;
}

#header {
    background-color: #000000;
    border-color: #000000;
    color: #e0e0e0;
}

#leftcol {
    background-color: #000000;
    border-color: #000000;
    color: #c4c4c4;
}

div.container {
    background-color: #000000;
    border-color: #000000;
}

div.gradient {
    background-color: #000000;
    border-color: #000000;
    color: #b5b5b5;
}

div.gradient p {
    background-color: #000000;
    border-color: #000000;
    color: #ffffff;
}

div.gradient p a {
    background-color: #000000;
    border-color: #000000;
    color: #c2c2c2;
}

div.item_info_column {
    background-color: #000000;
    border-color: #000000;
}

div.item_info_column h2 a {
    background-color: #000000;
    border-color: #000000;
    color: #800000;
    font-size: 100%;
}

div.transparent {
    background-color: #000000;
    border-color: #000000;
}

p a strong {
    background-color: #000000;
    border-color: #000000;
    color: #d4d4d4;
}

ul.item_list {
    background-color: #000000;
    border-color: #000000;
}

div.content_no_background {
    background-color: #000000;
}

#rightcol {
    border-color: #000000;
    background-color: #000000;
    color: #e0e0e0;
}

tbody tr td {
    background-color: #000000;
    border-color: #000000;
}

div.tab_nav {
    background-color: #000000;
    border-color: #000000;
}

div.tab_nav_contents {
    background-color: #000000;
    border-color: #000000;
}

div.corners {
    background-color: #000000;
    border-color: #000000;
}

a.current {
    background-color: #000000;
    border-color: #000000;
}

div.tab_nav_large_top {
    background-color: #000000;
    border-color: #000000;
}

ul.tabs li a {
    background-color: #000000;
    border-color: #000000;
}

div.msg {
    background-color: #000000;
    border-color: #000000;
    color: #d6d6d6;
}

div.tab_nav_large_bottom {
    background-color: #000000;
    border-color: #000000;
}

  html {
    background-color: #000000;
    border-color: #000000;
    color: #c7c7c7;
}

}
