=========================================================================================
  @name        Kaskus Elegant Dark Style (Kaskus EDS) 
  @description Recolor Kaskus Style with dark color.
  @author      yuzz181
  @version     1.00
  @date        30/12/2013
=========================================================================================
Created By yuzz181 (http://www.kaskus.co.id/profile/5577966), Support me with GRP, Thanks
*/

/* Recolor Style
====================*/

body, .hfeed .entry-head, #site-header .meta-header, user-tab-nav, .panel-wrapper.menu a.fold, .panel-wrapper.menu a.fold, .ac_over, .sec-tab>a, .blue.button, #forum-listing .listing-table thead {
	background:#444;
}
a, a:hover, #subforum  {
	color:#333;
}
#breadcrumbs-wrap, .hfeed:after {
	background:#ccc;
}
#breadcrumbs-wrap {
  	color:#222;
}
#site-footer, .live-posting:hover {
  	background:#222
}
#subforum, .user-content-wrapper {
 	background:#eee;
}
#forum-listing .listing-table, .blue.button, #forum-listing .listing-table thead  {
  	color:#eee
}
.panel-wrapper.menu a.fold:hover {
  	background:#111
}
body.forum .kasad-h, .live-posting {
  	background:#555;
}
.live-posting:hover {
	color:#ddd
}
.blue.button:hover {
	background:#000
}

/* Fix Element Color 
====================*/

#breadcrumbs-wrap, #site-header .meta-header, .hfeed .entry-head, #forum-listing .listing-table thead, #forum-listing .listing-table, body.forum .kasad-h  {
  	border:0px;
}

a:hover{font-style:none;text-decoration:none;}
.blue.button {box-shadow: 0 1px 0 #222,0 1px 0 rgba(255,255,255,0.5) inset;}
#site-header .meta-header {box-shadow: 0 1px 0 #333}

}
