css:
@-moz-document domain("news.ycombinator.com") {
::-moz-selection {
    background:orangered;
    color: #000;
}

* { font-family: sans-serif !important; font-size:inherit !important; }
body { margin: 0; }
td.title {
font-size: large !important;
text-shadow: black 0px 1px 3px !important;
}

td.default a,
td.title a:link, span.comment a:link{
  color:#DFDFDF !important;
  text-shadow: black 0px 1px 3px !important;
}

.comment, .c00 { color:#DFDFDF !important; }

font {
color:inherit !important;
font-family:inherit !important;
font-size:inherit !important;
text-shadow: black 0px 1px 3px !important;
}
td.title a:visited,  span.comment a:visited{
    color:#666666 !important; text-shadow: black 0px 1px 3px !important;
}

span.yclinks a,
td.default span.comhead a,
td.subtext a,
td.subtext span { font-size: 12px !important; color:#88AADD !important; text-shadow: black 0px 1px 3px !important; }
td.default span.comhead a,
td.subtext a { font-weight: bold; text-shadow: black 0px 1px 3px !important; }
tr.spacer { height: 10px !important; }

body > center > table > tbody > tr:first-child > td{
   background-color:#666666 !important;
}

body,
body > center > table{
   background-color: #222222 !important;
   width: 99% !important;
}

center > a:first-child:before {
  content:"▲";
  color: #777;
  font-family: monospace;
  font-weight: bold;
  padding: 3px;
  text-shadow: black 0px 1px 3px;
}
center > a:nth-child(3):before {
  content:"▼";
  color: #777;
  font-family: monospace;
  font-weight: bold;
  padding: 3px;
  text-shadow: black 0px 1px 3px;
}
.votearrow { display: none; }
}
