css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("community.us.playstation.com"),
domain("community.eu.playstation.com"){

.lia-quilt-forum-page, .pageSection, .lia-content,
.avatar, .signupCol, .mashArticle, .label,
.catRow 
{
background-color: #000000 !important; 
}
.thread-list tr, .lia-quilt-column-alley,
.lia-quilt-column, .ranking, .labels li,
.details, .lia-quilt-row, #signupInner,
.connectItem a, #moreCommunities
{
background: #000000 !important; 
}
.catRow span:hover
{
background: #0068bf !important; 
}
.thread-list td
{
background: #000000 !important; 
border-bottom: 1px #FFFFFF solid !important; 
}
.lia-quilt-column-main-right
{
border: 1px #FFFFFF solid !important; 
}
a, h1, h2, h3, h4, h5, h6, p, p span, font,
.repliesCountColumn, .messageAuthorColumn span, .kudosCountColumn, .latestPostDateColumn, .top,
.lia-message-body-content, labels, .UserName a,
.local-date, .local-time, .skip,
.status, .Online, .ranking-details
{
color: #FFFFFF !important; 
}
}

@-moz-document domain("blog.us.playstation.com") {

.post 
{
background: #000000 !important; 
}
.post h1 a, .post-title p a, .post-title p,
.post p, .post a, strong
{
color: #FFFFFF !important; 
}
}
