css:
@-moz-document domain("deviantart.com"), domain("sta.sh") {

	.regular.user-symbol {
		background: url('http://devilishdb.altervista.org/Leaf.png') 50% 50% no-repeat;
		background-size: cover;
		width: calc(1em + 2px);
		height: 1em;
		position: relative;
		top: calc(1 / 8 * 1em);
		-webkit-filter: invert() brightness(15%) hue-rotate(120deg) grayscale(25%);
	}

	.gr-body .regular.user-symbol {
		-webkit-filter: invert() grayscale(23%) brightness(35%) hue-rotate(147deg);
	}

	.friendmachine .regular.user-symbol {
		-webkit-filter: none;
	}

	.regular.user-symbol:hover {
		-webkit-filter: none;
	}

	.cc-name .regular.user-symbol:hover {
		-webkit-filter: invert() brightness(10%) hue-rotate(120deg) grayscale(25%);
	}

	.friendmachine .regular.user-symbol:hover {
		-webkit-filter: invert() brightness(20%) hue-rotate(120deg) grayscale(25%);
	}

	.text-ii .regular.user-symbol:hover, .ccusername .regular.user-symbol:hover {
		-webkit-filter: invert() brightness(25%) hue-rotate(120deg) grayscale(25%);
	}

	#oh-menu-deviant .user-symbol.regular, .friendmachine .controls .user-symbol.regular, .friendmachine .controls .user-symbol.regular:hover {
		top: 0;
		-webkit-filter: invert() brightness(82%) hue-rotate(157deg);
	}

	#oh-menu-deviant .user-symbol.regular {
		cursor: hand;
		cursor: pointer;
	}

	.symbols-modal h4 b:after {
		content: " with Leaf";
	}
}
